mapper.insert:

INSERT INTO BOARD_USER
 (USER_ID,
 USER_NAME,
 USER_PASSWORD)
 VALUES
 (#{userId},
 #{userName},
 #{userPassword}
 );

mapper.select:
SELECT user_id, user_password,access_right FROM BOARD_USER
WHERE user_id=#{userId}
AND user_password=#{userPassword};

SELECT USER_ID,USER_PASSWORD,access_right
FROM BOARD_USER
WHERE user_id=#{userId};
